INSERT INTO `games` VALUES ('248', 'ClayFighter 63⅓', null, '2', '09/30/1997', 'Danger Productions', 'Interplay', null, '|Fighting|', null, 'Get ready to crumble! Once again, the clay fighters prove that when you play a state-of-the-art fighter, you might lose lives, you might lose limbs, but there\'s no reason to lose your sense of humor! Fight in and out of 3D backgrounds. Get tossed out of a window and resume fighting outside. Choose form 12 eccentric fighters including brand new combatants and old favorites like \"Bad Mister Frosty\" and \"Bonker the Clown.\" Includes bonus \"secret characters\" who are so famous that a legal gag order prohibits us from revealing their identity. Move freely in over 12 highly detailed 3D environments.', null, null, '1290620944', null, null, 'T - Teen', '0', '0', '0', '0', '', 'no', '2011-05-31 04:18:40', '0', 'tv.com', 'No', null, null, '3', null, '', '', '', '', '', '', null, null, null, null);
