INSERT INTO `games` VALUES ('11', 'Army of Two', null, '0', null, 'EA Montreal', 'Electronic Arts', null, null, null, 'Focusing on cooperative strategies, Army of Two\'s main feature is the necessity to use coordinated teamwork to accomplish the game\'s goals. While the game is meant to be played with another human as a partner, a \"Partner Artificial Intelligence\" (PAI) is also included and programmed to follow the player\'s strategies. Dependence on a partner (whether human or PAI) is so pronounced that most objectives are impossible to complete without it.', null, null, '1325475324', null, null, 'M - Mature', '0', '0', '0', '0', '', 'no', '2012-01-02 03:35:24', '0', 'tv.com', 'No', null, null, '15', 'No', '', '', '', '', '', '', 'UCYTB_4k80c', null, null, null);
INSERT INTO `games` VALUES ('12', 'Assassin\'s Creed', null, '1', '11/14/2007', 'Ubisoft Montreal', 'Ubisoft', null, '|Action|Adventure|Sandbox|', null, 'The game centers on the use of a machine named the \"Animus\", which allows the viewing of the protagonist\'s genetic memories of his ancestors.\r\n\r\nThrough this plot device, details emerge of a struggle between two factions, the Knights Templar and the Assassins (Hashshashin), over an artifact known as a \"Piece of Eden\" and the game primarily takes place during the Third Crusade in the Holy Land in 1191.', '0', null, '1311299943', null, null, 'M - Mature', '0', '0', '0', '0', '', 'no', '2011-07-22 02:59:03', '0', 'tv.com', 'No', null, null, '1', null, '', '', '', '', '', '', null, null, null, null);
INSERT INTO `games` VALUES ('13', 'BioShock', null, '0', '08/21/2007', 'Irrational Games', '2K Games', null, null, null, 'Set in an alternate history 1960, the game places the player in the role of a plane crash survivor named Jack, who must explore the underwater city of Rapture, and survive attacks by the mutated beings and mechanical drones that populate it. The game incorporates elements found in role-playing and survival games, and is described by the developers and Levine as a \"spiritual successor\" to their previous titles in the System Shock series.', null, null, '1306552029', null, null, 'M - Mature', '0', '0', '0', '0', '', 'no', '2011-05-31 04:18:40', '0', 'tv.com', 'No', null, null, '1', null, '', '', '', '', '', '', null, null, null, null);
INSERT INTO `games` VALUES ('14', 'Dead Space: The Looooong Game', null, '1', '10/14/2008', 'Visceral Games', 'Electronic Arts', null, '|Shooter|', null, 'The player takes on the role of an engineer named Isaac Clarke, who battles a polymorphic, virus-like, alien infestation which turns humans into grotesque alien monsters called \"Necromorphs\", on board a stricken interstellar mining ship named the USG Ishimura.', null, null, '1315096491', null, null, 'M - Mature', '0', '0', '0', '0', '', 'no', '2011-09-04 01:34:51', '0', 'tv.com', 'No', null, null, '1', 'No', '', '', '', '', '', '', null, 'DS:TLG,,DS1', null, null);
INSERT INTO `games` VALUES ('15', 'Devil May Cry', null, '0', null, 'Capcom', 'Capcom', null, null, null, 'Set in modern times on the fictional Mallet Island, the story centers on the characters Dante and Trish and their quest to confront the demon lord Mundus. The story is told primarily through a mixture of cutscenes, which use the game\'s engine and several pre-rendered full motion videos.', '0', null, '1330766778', null, null, 'M - Mature', '0', '0', '0', '0', '', 'no', '2012-03-03 09:26:18', '0', 'tv.com', 'No', null, null, '11', 'No', '', '', '', '', '', '', null, null, null, null);
INSERT INTO `games` VALUES ('16', 'Devil May Cry 3: Dante\'s Awakening', null, '0', null, 'Capcom', 'Capcom', null, null, null, 'Set in modern times in an enchanted tower named Temen-ni-gru, the story centers on the dysfunctional relationship between Dante and his brother Vergil. The events of the game take place just as Dante has opened up the Devil May Cry agency, and before Dante\'s demonic heritage has reached its full potential.', null, null, '1306621033', null, null, 'M - Mature', '0', '0', '0', '0', '', 'no', '2011-05-31 04:18:40', '0', 'tv.com', 'No', null, null, '11', null, '', '', '', '', '', '', null, null, null, null);
INSERT INTO `games` VALUES ('17', 'Devil May Cry 4', null, '0', null, 'Capcom', 'Capcom', null, null, null, 'In the game, the player controls both Nero and Dante, and fights enemies in close combat using firearms, swords, and other weapons. The characters Lady and Trish from previous games in the series make appearances, along with new characters Nero, Kyrie, Credo, Gloria, and Agnus. The game is set after Devil May Cry but before Devil May Cry 2.', null, null, '1306578607', null, null, 'M - Mature', '0', '0', '0', '0', '', 'no', '2011-05-31 04:18:40', '0', 'tv.com', 'No', null, null, '12', null, '', '', '', '', '', '', null, null, null, null);
INSERT INTO `games` VALUES ('18', 'Driver: Parallel Lines', null, '0', null, 'Reflections Interactive', 'Ubisoft', null, null, null, 'Diverging from previous Driver games, Parallel Lines takes place in just one city, New York, instead of multiple cities, but in the middle of the story you change to different eras of the city - 1978 and 2006. Due to the underwhelming performance of Driv3r, particularly the often-derided on-foot sections, Parallel Lines returns to the formula used in earlier games in the series, focusing on driving, although shooting remains in the game.', null, null, '1306636948', null, null, 'M - Mature', '0', '0', '0', '0', '', 'no', '2011-05-31 04:18:40', '0', 'tv.com', 'No', null, null, '1', null, '', '', '', '', '', '', null, null, null, null);
