INSERT INTO `games` VALUES ('77', 'Burnout Paradise', null, '0', '01/22/2008', 'Criterion Games', 'Electronic Arts', null, '|Vehicle Simulation|', null, 'Paradise\'s gameplay is set in the fictional \"Paradise City\", an open world in which players can compete in several types of races. Players can also compete online, which includes additional game modes, such as \"Cops and Robbers\". Several free game updates introduce new features such as a time-of-day cycle and motorcycles. The game also features paid downloadable content in the form of new cars and the fictional \"Big Surf Island\".', null, null, '1306565384', null, null, 'E - Everyone', '0', '0', '0', '0', '', 'no', '2011-05-31 04:18:40', '0', 'tv.com', 'No', null, null, '1', null, '', '', '', '', '', '', null, null, null, null);
