INSERT INTO `games` VALUES ('96', 'Prototype', null, null, '06/09/2009', 'Radical Entertainment', 'Activision', null, '|Action|Adventure|', null, 'The game is set in New York City, where a virulent plague is spreading through Manhattan. Those infected are mutated into hideous monsters. The United States Marine Corps, under the command of the black ops organization Blackwatch, is dispatched to contain it. At the center of it is the protagonist, Alex Mercer, a shapeshifter with no memory of his past. Alex has the ability to absorb other individuals, taking on their biomass, memories, experiences, and physical forms. Parallel to the game\'s storyline is the ability to play the game as a sandbox-style video game giving the player freedom to roam Manhattan.', '0', null, '1289099561', null, null, 'M - Mature', '0', '0', '0', '0', '', 'no', '2011-05-31 04:18:40', '0', 'tv.com', 'No', null, null, '1', null, '', '', '', '', '', '', null, null, null, null);
INSERT INTO `games` VALUES ('101', 'S.T.A.L.K.E.R.: Clear Sky', null, '1', '09/05/2008', 'GSC Game World', 'Deep Silver', null, '|Action|Shooter|', null, 'S.T.A.L.K.E.R.: Clear Sky is a survival FPS game for PC based on a \'what-if\' scenario of the second Chernobyl Nuclear Power Plant accident. The game is set in 2011 and brings forth the events to have preceded the third campaign of Strelok to the Zone center. S.T.A.L.K.E.R.: Clear Sky introduces an alternative look onto the events of the original game and offers the player to try himself out as a mercenary s.t.a.l.k.e.r. in search of his own path in the world of S.T.A.L.K.E.R.', null, null, '1306158051', null, null, 'M - Mature', '0', '0', '0', '0', '', 'no', '2012-07-18 14:04:02', '0', 'tv.com', 'No', null, null, '1', null, '', '', '', '', '', '', null, 'Stalker: Clear Sky', null, null);
