INSERT INTO `games` VALUES ('109', 'The Legend of Zelda: Twilight Princess', null, '1', '11/19/2006', 'Nintendo', 'Nintendo', null, '|Action|Adventure|Role-Playing|', null, 'In the next chapter in the Legend of Zelda series, Link can transform into a wolf to scour the darkened land of Hyrule. With the help of Midna, a mysterious being, you must guide Link through hordes of foul creatures and challenging bosses using new moves and a new horseback combat system. Many puzzles stand between Link and the fulfillment of his quest, so you must sharpen your wits as you hunt for weapons and items.', '0', null, '1296604335', null, null, null, '0', '0', '0', '0', '', 'no', '2011-05-31 04:18:40', '0', 'tv.com', 'No', null, null, '2', null, '', '', '', '', '', '', null, null, null, null);
