INSERT INTO `games` VALUES ('195', 'Marvel vs. Capcom: Clash of Super Heroes', null, '0', '01/12/1998', 'Capcom', 'Capcom', null, '|Action|', null, 'The game takes place within the Marvel comic continuity, as Professor Charles Xavier calls out for heroes to stop him before he merges with the consciousness of Magneto and becomes the being known as Onslaught, the final boss.  While the gameplay was typical of the Marvel vs. Capcom series, Marvel vs. Capcom was distinguishable by two features: the ability to summon assist characters, and the Duo Team Attack.', null, null, '1306631569', null, null, 'T - Teen', '0', '0', '0', '0', '', 'no', '2011-05-31 04:18:40', '0', 'tv.com', 'No', null, null, '16', null, '', '', '', '', '', '', null, null, null, null);
INSERT INTO `games` VALUES ('196', 'Metal Gear Solid', null, '1', '09/03/1998', 'Konami Computer', 'Konami', null, '|Action|', null, 'Metal Gear Solid follows Solid Snake, a soldier who infiltrates a nuclear weapons facility to neutralize the terrorist threat from FOXHOUND, a renegade special forces unit. Snake must liberate two hostages, the head of DARPA and the president of a major arms manufacturer, confront the terrorists, and stop them from launching a nuclear strike.', null, null, '1306622841', null, null, 'M - Mature', '0', '0', '0', '0', '', 'no', '2011-05-31 04:18:40', '0', 'tv.com', 'No', null, null, '10', null, '', '', '', '', '', '', null, null, null, null);
