INSERT INTO `games` VALUES ('232', 'Super Mario RPG: Legend of the Seven Stars', null, '1', '05/13/1996', 'Square', 'Nintendo', null, '|Role-Playing|', null, 'Set in the Mushroom Kingdom and featuring its cast of well-known characters, this adventure RPG begins with a familiar scenario: Mario must rescue Princess Toadstool from Bowser. However, a mysterious new enemy named Smithy soon appears, and Mario and Bowser are forced to fight as allies for the first time in their long history. In the quest to defeat their powerful new foe, they’ll journey across a wide variety of locations (a town of mining moles, a tropical forest, a thundering waterfall—just to name a few) and tackle a mix of obstacles requiring both expert timing and strategy to overcome. Along the way, they’ll gain experience and grow more powerful, while also meeting new characters whose unique abilities will be needed to take on Smithy and his gang.', '0', null, '1289279988', null, null, 'E - Everyone', '0', '0', '0', '0', '', 'no', '2011-05-31 04:18:40', '0', 'tv.com', 'No', null, null, '6', null, '', '', '', '', '', '', null, null, null, null);
