INSERT INTO `games` VALUES ('261', 'Killer Instinct Gold', null, '2', '11/25/1996', 'Rare Ltd.', 'Nintendo', null, '|Fighting|', null, 'Killer Instinct Gold rocks the gaming world with its earth-shattering moves and unbelievable graphics. Your battles unfold with lightning-fast action and fluid character animation at 60 frames per second. We faithfully duplicated all the features that made Killer Instinct 2 an arcade hit. We\'ve even thrown in a complete training mode to school you on all the moves! The eye-popping graphics, the explosive hits and jaw-dropping combos make Killer Instinct Gold the only fighting game you\'ll ever want in your house!', null, null, '1290621981', null, null, 'T - Teen', '0', '0', '0', '0', '', 'no', '2011-05-31 04:18:40', '0', 'tv.com', 'No', null, null, '3', null, '', '', '', '', '', '', null, null, null, null);
INSERT INTO `games` VALUES ('262', 'Kirby 64: The Crystal Shards', null, '4', '6/26/2000', 'HAL Laboratory', 'Nintendo', null, '|Adventure|', null, 'Kirby\'s first 3-D adventure is also his Nintendo 64 debut, and it finds the always-versatile hero battling a new enemy called Dark Matter. Dark Matter is after a distant land\'s powerful crystal, but a young fairy named Ribbon attempts to save it by escaping with the gem to Dream Land. Now the crystal has been broken, and it\'s scattered around the world. Take control of Kirby and help him journey across six worlds, battling a wide variety of enemies and challenging bosses, as he tries to collect all 100 pieces of the shattered crystal.', null, null, '1301158865', null, null, 'E - Everyone', '0', '0', '0', '0', '', 'no', '2011-05-31 04:18:40', '0', 'tv.com', 'No', null, null, '3', null, '', '', '', '', '', '', null, null, null, null);
