INSERT INTO `games` VALUES ('270', 'Mario Party 2', null, '4', '12/17/1999', 'Hudson Soft', 'Nintendo', null, '|Adventure|Puzzle|Strategy|', null, 'Mario and the gang are back for another round of Bowser-bashin\' party action! Watch as your favorite Nintendo characters don different duds for each of the five all-new Adventure Boards! A slew of new tricks and devices bring new levels of challenge and excitement to board game play. New board maps, new Mini-Games, new action and new surprises means a whole new batch of fun! Get ready to unleash your best Hip Drops, hammer swings and high-flying high junks for another round of frenzied multi-player action!', null, null, '1290622177', null, null, 'E - Everyone', '0', '0', '0', '0', '', 'no', '2011-05-31 04:18:40', '0', 'tv.com', 'No', null, null, '3', null, '', '', '', '', '', '', null, null, null, null);
