INSERT INTO `games` VALUES ('275', 'Ardy Lightfoot', null, '1', '11/26/1993', 'ASCII Entertainment', 'ASCII Entertainment', null, '|Action|Adventure|', null, 'The story of the game is that the sacred rainbow has shattered into seven pieces and it\'s up to Ardy to obtain them all again. Whoever collects all seven pieces will receive one wish. An evil king named Visconti has already gotten one piece and is searching for the others. To this end he sends out various creatures and henchmen such as Beecroft, Catry and others. These creatures form the opponents for Ardy during the game. Ardy is assisted by friends along the way like the elder (unknown name), Nina, and a mysterious adventurer named Don Jacoby.', null, null, '1290588713', null, null, 'E - Everyone', '0', '0', '0', '0', '', 'no', '2011-05-31 04:18:40', '0', 'tv.com', 'No', null, null, '6', null, '', '', '', '', '', '', null, null, null, null);
INSERT INTO `games` VALUES ('276', 'Battle Clash', null, '2', '06/21/1993', 'Nintendo', 'Nintendo', null, '|Shooter|', null, 'Battle Clash is a game that uses the Super Scope light gun peripheral. In this game, you fight in a futuristic version of the world as a warrior named Michael Anderson, who takes part in a competition simply known as the \"Battle Game\". Contestants in the Battle Game fight using mobile Standing Tanks (or STs for short), that come with a variety of weapon systems and forms.', '0', null, '1289712528', null, null, null, '0', '0', '0', '0', '', 'no', '2011-05-31 04:18:40', '0', 'tv.com', 'No', null, null, '6', null, '', '', '', '', '', '', null, null, null, null);
