INSERT INTO `games` VALUES ('299', 'Super Metroid', null, '1', '04/18/1994', 'Nintendo R&D1 Intelligent Systems', 'Nintendo', null, '|Adventure|', null, 'Chronologically, Super Metroid takes place seventh in the Metroid universe and immediately after the events of Metroid II: Return of Samus, and begins with a narrative by bounty hunter Samus Aran. Samus describes how a Metroid larva hatched from an egg and immediately imprinted upon her, believing her to be its mother. She brought the larva to Ceres Space Colony, where scientists learned that they could harness its power. Just after she left the colony, she received a distress call and returned to find the scientists dead and the larva stolen. The game begins as she follows the leader of the Space Pirates, Ridley, to the planet Zebes, where she searches for the stolen larva in a network of caves.', null, null, '1302613253', null, null, 'E - Everyone', '0', '0', '0', '0', '', 'no', '2011-05-31 04:18:40', '0', 'tv.com', 'No', null, null, '6', null, '', '', '', '', '', '', null, null, null, null);
