INSERT INTO `games` VALUES ('302', 'The Itchy & Scratchy Game', null, '1', '1995', 'Bits Studios', 'Acclaim Entertainment', null, '|Action|', null, 'Itchy & Scratchy is a single player platform side-scroller. The player controls Itchy, the mouse, and must defeat Scratchy, the cat, with a variety of weapons. Itchy caries a default mallet, but can pick up additional weapons on particular levels. Each of the seven levels takes place in a different fictional location and is designed as a maze of doors and platforms. After defeating Scratchy, he usually will return with a special contraption to attack Itchy, and must therefore be defeated again.', null, null, '1306626596', null, null, 'E - Everyone', '0', '0', '0', '0', '', 'no', '2011-05-31 04:18:40', '0', 'tv.com', 'No', null, null, '18', null, '', '', '', '', '', '', null, null, null, null);
INSERT INTO `games` VALUES ('314', '2-in-1 Super Spike V-Ball & Nintendo World Cup', null, '0', '02/01/1990', 'Technos', 'Nintendo', null, null, null, '2 in 1 game. Super Spike V\'Ball / Nintendo World Cup was a pack-in game included with the Nintendo Entertainment System Sports Set. The package included a standard NES console, four game controllers, and the Four Score Multiplayer Adapter. The cart included two games developed by Technos: # Super Spike V\'Ball, a two-on-two beach volleyball game where you played on a tournament, ending with the world championship. The game supported up to four players on one match at a time; # Nintendo World Cup, a soccer game where you (and a friend) chose to represent a team and played several matches until you won the World Cup. The game featured little to no rules, which means that you could hit your opponents as much as you want, without fear of the Ref getting in the way. Both games were compatible with the included Four Score NES multiplayer adapter.', null, null, '1306572759', null, null, 'E - Everyone', '0', '0', '0', '0', '', 'no', '2011-05-31 04:18:40', '0', 'tv.com', 'No', null, null, '7', null, '', '', '', '', '', '', null, null, null, null);
