INSERT INTO `games` VALUES ('327', 'The Adventures of Bayou Billy', null, null, '08/12/1988', 'Konami', 'Konami', null, '|Vehicle Simulation|Shooter|', null, 'The player takes control of the title character, Billy West, who must fight save to his girlfriend Annabelle Lane from the gang of Godfather Gordon. There are a total of ten stages in all: six side-scrolling beat-em-up stages (or street fighting stages, as the game actually dubs them), two light gun shooting (shoot-\'em-up) stages, and two action driving stages.', '0', null, '1288414089', null, null, null, '0', '0', '0', '0', '', 'no', '2011-05-31 04:18:40', '0', 'tv.com', 'No', null, null, '7', null, '', '', '', '', '', '', null, null, null, null);
