INSERT INTO `games` VALUES ('370', 'Ninja Gaiden II: The Dark Sword of Chaos', null, '0', '05/01/1990', 'Tecmo', 'Tecmo', null, '|Action|Adventure|', null, 'Dynamic action scenes! Split your body into three, destroy all enemies with the Invincible Fire Wheel and other new Ninja fighting techniques! Ninja Gaiden II gives you the feel of real Ninjutsu! Exciting cinema display format! Ninja Gaiden II uses the same Cinema Display Format as the original. The fiendish plot unfolds in a movie-like sequence. Action, drama, revenge. Just wait till you get to the end!', null, null, '1290878580', null, null, null, '0', '0', '0', '0', '', 'no', '2011-05-31 04:18:40', '0', 'tv.com', 'No', null, null, '7', null, '', '', '', '', '', '', null, null, null, null);
INSERT INTO `games` VALUES ('371', 'Paperboy', null, '0', '12/01/1988', 'Tengen', 'Mindscape Inc.', null, '|Action|', null, 'Hop on your bike for a free-wheeling ride up the avenues of not-so-typical suburbia. There are papers to be delivered, robbers to be foiled, and fame and fortune to be won as you brave the mean streets. Avoid motorcycles, tricycles, traffic, tires, gratings, curbs, dogs, skateboarders, breakdancers, bad guys, and other hazards as you deliver to your customers. Earn bonus points by hitting targets along the obstacle course at the end of your route. With superb animation and sound effects, Paperboy brings home all the thrills, spills, challenge, and excitement you loved in the arcades.', null, null, '1290879095', null, null, null, '0', '0', '0', '0', '', 'no', '2011-05-31 04:18:40', '0', 'tv.com', 'No', null, null, '7', null, '', '', '', '', '', '', null, null, null, null);
