INSERT INTO `games` VALUES ('393', 'Quake II', null, '0', '05/31/1999', 'Raster', 'Activision', null, '|Shooter|', null, 'Authentic Nintendo 64 effects and newer, fiercer levels pack stunning graphic detail into an already vicious version of Quake II! Featuring Two-, Three-, and Four-player Deathmatch, fully customizable controls and save game features, this masterpiece takes gaming to an undefined level! Eerie environments of light and shadow loom within newly designed, mission-based levels not found in the PC version. Incredible engine speed and fluidity intensify each level to a fast-paced, high-action gameplay experience!', null, null, '1306638690', null, null, 'M - Mature', '0', '0', '0', '0', '', 'no', '2011-05-31 04:18:40', '0', 'tv.com', 'No', null, null, '1', null, '', '', '', '', '', '', null, null, null, null);
INSERT INTO `games` VALUES ('394', 'Rampage World Tour', null, '3', '03/30/1998', 'Saffire', 'Midway', null, '|Action|', null, 'A wild smash-\'em-up romp with universal appeal! Simple enough for any player. Plenty of depth and challenge to appeal to serious gamers as well. Bring a friend or two on a non-stop RAMPAGE while you inflict some major damage and destruction. Demolish buildings, swat down aircraft, eat people and rack up points, while destroying entire cities! More than 130 standard levels, 14 bonus levels, 4 grudge match levels and many hidden levels! Special bosses and some tasty humans give Lizzy, George or Ralph a major health boost.', null, null, '1290643653', null, null, 'T - Teen', '0', '0', '0', '0', '', 'no', '2011-05-31 04:18:40', '0', 'tv.com', 'No', null, null, '3', null, '', '', '', '', '', '', null, null, null, null);
