INSERT INTO `games` VALUES ('404', 'Super Smash Bros.', null, '4', '01/21/1999', 'HAL Laboratory', 'Nintendo', null, '|Fighting|', null, 'Smash Bros players seek to knock opposing characters off the stage. In Super Smash Bros., characters have a damage total, represented by a percentage value, which rises as they take damage and can exceed 100%, but maxes out at 999%. As a character\'s percentage rises, the character can be knocked progressively farther by an opponent\'s attacks. To KO an opponent, the player must send that character flying off the edge of the stage, which is not an enclosed arena but rather an area with open boundaries, usually a set of suspended platforms.', null, null, '1290643906', null, null, 'E - Everyone', '0', '0', '0', '0', '', 'no', '2011-05-31 04:18:40', '0', 'tv.com', 'No', null, null, '3', null, '', '', '', '', '', '', null, null, null, null);
