INSERT INTO `games` VALUES ('458', 'Bram Stoker\'s Dracula', null, '1', '09/01/1993', 'Psygnosis, Traveller\'s Tales', 'Sony Imagesoft, Psygnosis', null, '|Action|', null, 'In the game the player takes on the role of Jonathan Harker. Throughout the levels, Abraham Van Helsing will help Jonathan in his quest by providing advanced weapons. With the exception of the MS-DOS version, the game is of the side-scrolling genre. In the game, Jonathan Harker fights Lucy Westenra as a vampiress, Count Dracula\'s three brides, Dracula\'s coach driver, Dracula\'s fire-breathing dragon, Renfield and even Dracula himself and also in some different forms, such as him in his bat form, his young form and his evil wolf form. Levels in the game include the Romanian countryside, a rat-infested old village inn, Dracula\'s castle, Dracula\'s cavernous vaults, Dracula\'s misty catacombs, various locations in London, Lucy\'s crypt, a graveyard and Carfax Abbey.', null, null, '1306657335', null, null, 'E - Everyone', '0', '0', '0', '0', '', 'no', '2011-05-31 04:18:40', '0', 'tv.com', 'No', null, null, '7', null, '', '', '', '', '', '', null, null, null, null);
INSERT INTO `games` VALUES ('459', 'Bubsy', null, '0', '05/01/1990', 'Accolade', 'Sega', null, '|Adventure|', null, 'The plot focuses on a race of fabric-stealing aliens called \"Woolies\", who have stolen the world\'s yarn ball supply (especially Bubsy\'s, who owns the world\'s largest collection). Naturally, Bubsy does not take too kindly to the theft and sets out to \"humble\" the Woolies.', null, null, '1306565130', null, null, 'E - Everyone', '0', '0', '0', '0', '', 'no', '2011-05-31 04:18:40', '0', 'tv.com', 'No', null, null, '18', null, '', '', '', '', '', '', null, null, null, null);
