INSERT INTO `games` VALUES ('509', 'Bust-A-Move 4', null, '0', '2000', 'Cyber Front', 'Taito', null, '|Puzzle|Strategy|', null, 'Puzzle Bobble 4 (also known as Bust-a-Move 4 in North America and Europe) is the third sequel to the video game Puzzle Bobble and is the final appearance of the series on the Arcade, PlayStation and Dreamcast. The game is also the final title to be recognizably similar in presentation to the original.\r\n\r\nBuilding upon the success of Puzzle Bobble 3, the game adds a pulley system that requires two sets of bubbles, attached to either side of a rope hanging across two pulleys. The game contains a story mode for single player play.', null, null, '1306565479', null, null, 'E - Everyone', '0', '0', '0', '0', '', 'no', '2011-05-31 04:18:40', '0', 'tv.com', 'No', null, null, '10', null, '', '', '', '', '', '', null, null, null, null);
INSERT INTO `games` VALUES ('510', 'Casper', null, '1', '08/01/1996', 'Absolute Entertainment', 'Natsume', null, '|Action|Adventure|', null, 'The plot centers around the \"spoiled, grasping inheritor of Whipstaff Manor\" - Carrigan Crittenden. She\'s after the treasure she thinks is hidden in the walls. Casper will need the help of Dr James Harvey - therapist to the dead - if he has any hope of succeeding in his adventures.', null, null, '1306632363', null, null, 'E - Everyone', '0', '0', '0', '0', '', 'no', '2011-05-31 04:18:40', '0', 'tv.com', 'No', null, null, '10', null, '', '', '', '', '', '', null, null, null, null);
