INSERT INTO `games` VALUES ('528', 'Final Fantasy Anthology', null, '1', '10/05/1999', 'Square', 'Square Enix', null, '|Role-Playing|', null, 'Final Fantasy Anthology combines two titles from the Final Fantasy series and offers PlayStation owners a glimpse into the past. Huge worlds, in-depth storylines, and innovative battle and magic systems, and an incredible cast of characters all make for a deep gaming experience. The inclusion of Final Fantasy V marks the first time this title has appeared on a console in North America. Final Fantasy VI, originally released as Final Fantasy III on the SNES system in the U.S., makes its first appearance on the PlayStation. Both games in this compilation feature never-before-seen CG movies, adding to the appeal of these classics.', null, null, '1290280520', null, null, 'T - Teen', '0', '0', '0', '0', '', 'no', '2011-05-31 04:18:40', '0', 'tv.com', 'No', null, null, '10', null, '', '', '', '', '', '', null, null, null, null);
