INSERT INTO `games` VALUES ('619', 'Tomb Raider II', null, '1', '11/01/1997', 'Core Design', 'Eidos Interactive', null, '|Action|Adventure|', null, 'The story of Tomb Raider II surrounds the mythical Dagger of Xian, a weapon which was used by an Emperor of China to command his army. By plunging the weapon into its owner\'s heart, the weapon has the power to turn its bearer into a dragon. A flashback reveals that the last battle which was fought with the Dagger ended in defeat when the warrior monks of Tibet succeeded in removing the knife from the Emperor\'s heart. The Dagger was then returned to its resting place within the Great Wall.', null, null, '1306625562', null, null, 'T - Teen', '0', '0', '0', '0', '', 'no', '2011-05-31 04:18:40', '0', 'tv.com', 'No', null, null, '10', null, '', '', '', '', '', '', null, null, null, null);
