INSERT INTO `games` VALUES ('683', 'Risk', null, '0', '04/02/1994', 'Sculptured Software', 'Parker Brothers', null, '|Strategy|', null, 'Players attempt to capture territories from other players by rolling higher die numbers. The game is won when one player has managed to occupy every territory.', null, null, '1306661281', null, null, 'E - Everyone', '0', '0', '0', '0', '', 'no', '2011-05-31 04:18:40', '0', 'tv.com', 'No', null, null, '18', null, '', '', '', '', '', '', null, null, null, null);
INSERT INTO `games` VALUES ('684', 'Beauty & The Beast: Roar of the Beast', null, null, '1993', 'Sunsoft', 'Sunsoft', null, null, null, 'As the Beast, the player must successfully complete several levels, based on scenes from the film, in order to protect the castle from invading villagers and forest animals and rescue Belle from the evil Gaston', null, null, '1286003238', null, null, 'E - Everyone', '0', '0', '0', '0', '', 'no', '2011-05-31 04:18:40', '0', 'tv.com', 'No', null, null, '18', null, '', '', '', '', '', '', null, null, null, null);
INSERT INTO `games` VALUES ('685', 'Rocket Knight Adventures', null, null, '06/16/1993', 'Konami', 'Konami', null, '|Shooter|', null, 'Gameplay is often a side scrolling platform, with linear runs through levels. The player is able to jump, attack with Sparkster\'s sword, either directly or by \"shooting\" energy forward when swung, or charge up their rocket pack and go flying in one of the 8 standard directions, depending of the direction the player presses (if no direction is pressed, Sparkster performs a stationary spinning attack).\r\nLevels are occasionally switched up with alternate styles of gameplay. Some levels are horizontal scrolling shooters (akin to Gradius, often with in-level references to that game), while one level has the player control a large machine to duel with Axel Gear.', null, null, '1286003513', null, null, 'E - Everyone', '0', '0', '0', '0', '', 'no', '2011-05-31 04:18:40', '0', 'tv.com', 'No', null, null, '18', null, '', '', '', '', '', '', null, null, null, null);
INSERT INTO `games` VALUES ('686', 'Roger Clemens\' MVP Baseball', null, null, '1991', 'Sculptured Software', 'Acclaim', null, null, null, 'The game features 26 teams to use, an exhibition mode and a regular season mode. The game had the ability to \"save\" your career progress by giving you a password that you could enter at the menu screen, when you wanted to continue the season.', null, null, '1286003760', null, null, 'E - Everyone', '0', '0', '0', '0', '', 'no', '2011-05-31 04:18:40', '0', 'tv.com', 'No', null, null, '18', null, '', '', '', '', '', '', null, null, null, null);
INSERT INTO `games` VALUES ('687', 'College Slam', null, null, '1996', 'Iguana Entertainment', 'Acclaim', null, null, null, 'Each team starts out by picking any 2 out of 5 players to play with during the game. During timeouts, and at half-time, the player has a choice to make substitutions. When a player makes 2 baskets in a row, the announcer says \"He\'s heating up\", and if he makes 3 baskets in a row without the other team scoring, he says \"He\'s on fire!\", which makes it easier to score.\r\nIn the season mode, the player can pick from 44 teams, and then play a 20 game season against quality competition. In the tournament mode, 16 teams compete for a chance to win the national championship. The player also has the ability to edit teams and players. The game itself is not realistic, as players can easily make full-courts shots and occasionally, players get struck by lightning as they dunk. The final score of the games are usually in the middle to high 100s.', null, null, '1286003986', null, null, 'E - Everyone', '0', '0', '0', '0', '', 'no', '2011-05-31 04:18:40', '0', 'tv.com', 'No', null, null, '18', null, '', '', '', '', '', '', null, null, null, null);
INSERT INTO `games` VALUES ('688', 'Shadow Blasters', null, null, '1990', 'Sigma Enterprises', 'Sage\'s Creation', null, null, null, 'The evil god Ashura has unleashed hordes of monsters and demons onto Earth, hoping to take control of the planet as well as the hearts of men. The powerful god Hyprion has recruited four of Earth\'s most powerful warriors and assembled them into a team to fight the encroaching darkness. The player assumes control of this group of warriors in order to defeat the armies and their god, Ashura.', '0', null, '1296522056', null, null, 'E - Everyone', '0', '0', '0', '0', '', 'no', '2011-05-31 04:18:40', '0', 'tv.com', 'No', null, null, '18', null, '', '', '', '', '', '', null, null, null, null);
