INSERT INTO `games` VALUES ('729', 'Wonder Boy in Monster World', null, null, '1993', 'Westone', 'Sega', null, '|Role-Playing|', null, 'Wonder Boy In Monster World puts you in control of Shion in his quest to save Monster World from the evil BioMeka. It controls like your standard platform game: run, jump, and kill enemies. The game is filled with RPG elements such as talking to townsfolk, collecting money to buy items, extending your life bar, and equipping a large variety of armor, weapons and magic.\r\n\r\nShion travels through the many interconnected regions of Monster World, all the while collecting increasingly powerful equipment in the form of many different swords, spears, shields, suits of armor, and boots.', null, null, '1305707104', null, null, 'E - Everyone', '0', '0', '0', '0', '', 'no', '2011-05-31 04:18:40', '0', 'tv.com', 'No', null, null, '18', null, '', '', '', '', '', '', null, null, null, null);
