INSERT INTO `games` VALUES ('749', 'Age of Empires II: The Age of Kings', null, null, '09/30/1999', 'Ensemble Studios', 'Microsoft Game Studios', null, '|Strategy|', null, 'The Age of Kings focuses on building towns, gathering resources, creating armies, and destroying enemy units and buildings. Players conquer rival towns and empires as they advance one of 13 civilizations through four \"Ages\": the Dark Age, the Feudal Age, the Castle Age (The Middle Ages), and the Imperial Age, reminiscent of the Renaissance—a 1000 year timeframe.', null, null, '1286351051', null, null, 'T - Teen', '0', '0', '0', '0', '', 'no', '2011-05-31 04:18:40', '0', 'tv.com', 'No', null, null, '1', null, '', '', '', '', '', '', null, null, null, null);
INSERT INTO `games` VALUES ('750', 'Age of Empires III', null, null, '10/18/2005', 'Ensemble Studios', 'Microsoft Game Studios', null, '|Strategy|', null, 'Age of Empires III is mostly set in the New World during the colonial era, between circa 1492 and 1850.   In the style of previous games of the series, the game requires the player to develop a European, Asian or North American nation\'s colony from a basic settlement to an empire, progressing through \"Ages\"—stages of technological development—and destroying the enemy\'s colony. There are two main branches to gameplay: the economy, characterized by the collection of resources, and production of civilian units—which usually gather resources; and the military which involves the production of military units, and the conflicts between armies of rival teams or factions.', null, null, '1301104120', null, null, 'T - Teen', '0', '0', '0', '0', '', 'no', '2011-05-31 04:18:40', '0', 'tv.com', 'No', null, null, '1', null, '', '', '', '', '', '', null, null, null, null);
INSERT INTO `games` VALUES ('751', 'Battlefield 2', null, '4', '06/21/2005', 'Digital Illusions CE', 'EA Games', null, '|Shooter|', null, 'The story takes place in the early 21st century during a fictional world war between various power blocks: China, the European Union (playable as of the 1.50 patch), the fictional Middle Eastern Coalition (MEC), Russia (playable only in the Special Forces expansion) and the United States. In-game, the European Union and the United States fight China and the MEC. It is known that in the game\'s story, the EU and the US are allies and the EU has negotiated a peace deal with Russia but it is unknown if China and the MEC are allies. The game takes place in different fronts, as the Middle East and China are being invaded by US and EU forces, and the United States is being invaded by Chinese and MEC forces.', null, null, '1304022829', null, null, 'T - Teen', '0', '0', '0', '0', '', 'no', '2011-05-31 04:18:40', '0', 'tv.com', 'No', null, null, '1', null, '', '', '', '', '', '', null, null, null, null);
