INSERT INTO `games` VALUES ('772', 'SimCity', null, '0', '1989', 'Maxis', 'Maxis', null, '|Construction and Management Simulation|Strategy|', null, 'The player can mark land as being zoned as commercial, industrial, or residential, add buildings, change the tax rate, build a power grid, build transportation systems and take many other actions, in order to enhance the city.\r\nAlso, the player may face disasters including flooding, tornadoes, fires (often from air disasters or even shipwrecks), earthquakes and attacks by monsters. In addition, monsters and tornadoes can trigger train crashes by running into passing trains. Later disasters in the game\'s sequels included lightning strikes, volcanoes, meteors and attack by extraterrestrial craft.\r\nIn the SNES version and later, one can also build rewards when they are given to them, such as a mayor\'s mansion, casino, etc.', null, null, '1306622413', null, null, 'E - Everyone', '0', '0', '0', '0', '', 'no', '2011-05-31 04:18:40', '0', 'tv.com', 'No', null, null, '1', null, '', '', '', '', '', '', null, null, null, null);
INSERT INTO `games` VALUES ('773', 'SimCity 4', null, null, '01/14/2003', 'Maxis', 'Electronic Arts', null, '|Construction and Management Simulation|Strategy|', null, 'The game allows players to create a region of land by terraforming, and then to design and build a settlement which can grow into a city. Players can zone different areas of land as commercial, industrial, or residential development, as well as build and maintain public services, transport and utilities. For the success of a city players must manage its finances, environment, and quality of life for its residents. SimCity 4 introduces night and day cycles and other special effects for the first time in the SimCity series. External tools such as the Building Architect Tool (BAT) allow custom third party buildings and content to be added to the gameplay.', null, null, '1286611165', null, null, 'E - Everyone', '0', '0', '0', '0', '', 'no', '2011-05-31 04:18:40', '0', 'tv.com', 'No', null, null, '1', null, '', '', '', '', '', '', null, null, null, null);
