INSERT INTO `games` VALUES ('826', 'Dead Rising 2', null, '0', '09/28/2010', 'Blue Castle Games', 'CAPCOM', null, '|Action|Adventure|', null, 'Several years have passed since the Wilamette incident, and Dead Rising 2 shifts the action from the everyday world of mid-West America to the glitz and glamour of Fortune City, America\'s latest and greatest entertainment playground. People flock to Fortune City from around the globe to escape from reality and the chance to win big and for some, this means competing in Terror is Reality.\r\n\r\nLike millions of Americans, former national motocross champion Chuck Greene is gripped by the TV sensation that is Terror is Reality. Hosted by the flamboyant Tyrone King, Terror is Reality pits ordinary members of the public against an arena full of zombies with a simple challenge – kill more zombies than your opponents and stay alive with the winner collecting big money and the chance to come back and secure even greater prizes. So, what is it that has forced Chuck to come to Fortune City and risk his life in the modern day gladiatorial contest, is he trying to recapture the fame of his motocross days, does he have a reason to hate zombies, or is it simply the lure of big money?', null, null, '1306635334', null, null, 'M - Mature', '0', '0', '0', '0', '', 'no', '2011-05-31 04:18:40', '0', 'tv.com', 'No', null, null, '1', null, '', '', '', '', '', '', null, null, null, null);
INSERT INTO `games` VALUES ('828', 'R.U.S.E.', null, '4', '09/07/2010', 'Eugen Systems', 'Ubisoft', null, '|Strategy|', null, 'Developed by Eugen Systems, R.U.S.E is the first strategy game where your ability to deceive and manipulate your enemies determines your nation\'s fate. Lead your army to victory by camouflaging your troops, luring your opponent with decoy units, sabotaging enemy logistics and much more. Strategy has never been so intuitive and deep. R.U.S.E features cutting edge graphics and effects, the incredible IRISZOOM Engine, immersive combat and a simplified interface that allows you to determine your nation\'s strategy and fight to defeat your enemies.', '0', null, '1304024971', null, null, 'T - Teen', '0', '0', '0', '0', '', 'no', '2011-05-31 04:18:40', '0', 'tv.com', 'No', null, null, '1', null, '', '', '', '', '', '', null, null, null, null);
