INSERT INTO `games` VALUES ('847', 'Halo Wars', null, '0', '03/03/2009', 'Ensemble Studios', 'Microsoft Game Studios', null, '|Strategy|', null, 'The year is 2531, twenty years prior to the events in Halo: Combat Evolved and the Covenant have found something on the planet Garvest. The UNSC ship \"Spirit of Fire\" is sent to investigate and stop whatever the enemy is up to. What the crew finds could be the only thing that stands between humanity and certain annihilation by the Covenant. Command large armies, lead them into battle, control their every move and use their abilities to gain the upper hand in combat.', '0', null, '1297121491', null, null, 'T - Teen', '0', '0', '0', '0', '', 'no', '2011-05-31 04:18:40', '0', 'tv.com', 'No', null, null, '15', null, '', '', '', '', '', '', null, null, null, null);
