INSERT INTO `games` VALUES ('851', 'Watchmen: The End Is Nigh', null, '2', '07/21/2009', 'Deadline Games', 'Warner Bros. Games', null, '|Action|Fighting|', null, 'The game allows players to take on the roles of either Rorschach or Nite Owl II in single player or cooperative multiplayer. Rorschach and Nite Owl are the only playable characters in the game\'s first episode, which comprises six \"chapters.\" Cutscenes that look like animated comic panels, similar to those seen in the Watchmen motion comics released on iTunes, bookend each chapter. Two of the film\'s actors, Patrick Wilson and Jackie Earle Haley, provide their voices for their characters Nite Owl and Rorschach, respectively. The game features a mix of beat-em-up and puzzle gameplay, with the two characters having different strengths and abilities. Rorschach is faster with unconventional attacks and makes use of improvised weapons like crowbars and baseball bats; Nite Owl is slower but has a solid martial arts method and uses technological devices, such as \"screecher bombs\", and the grappling gun. The characters must work cooperatively to pass puzzles and defeat enemies.', null, null, '1306566726', null, null, 'M - Mature', '0', '0', '0', '0', '', 'no', '2011-05-31 04:18:40', '0', 'tv.com', 'No', null, null, '1', null, '', '', '', '', '', '', null, null, null, null);
INSERT INTO `games` VALUES ('852', 'Colin McRae: Dirt 2', null, '4', '12/01/2009', 'Codemasters', 'Codemasters', null, '|Racing|', null, 'Following on the success of the original Dirt as well as a decade of videogame development in partnership with the late Colin McRae, DiRT 2 explores various disciplines of off-road racing. Dirt 2 features a roster of contemporary off-road events, taking players to the most diverse and challenging real-world environments. This World Tour has players competing in aggressive multi-car and intense solo races at extraordinary new locations, from canyon racing and jungle trails to city stadium-based events.', '0', null, '1296603207', null, null, 'T - Teen', '0', '0', '0', '0', '', 'no', '2011-05-31 04:18:40', '0', 'tv.com', 'No', null, null, '1', null, '', '', '', '', '', '', null, null, null, null);
