INSERT INTO `games` VALUES ('867', 'Dragon Age: Origins', null, '0', '11/03/2009', 'BioWare', 'Electronic Arts', null, '|Action|Adventure|Role-Playing|', 'Tim Russ, Steve Valentine, Kate Mulgrew and Claudia Black', 'Dragon Age: Origins is an epic story of magic, danger and a fight for survival told by a legendary games company. BioWare, creators of Mass Effect, Baldur\'s Gate and Star Wars: Knights Of The Old Republic, bring you their best adventure yet. As a Grey Warden, you protect the land of Ferelden from the Darkspawn. When the demons attack the land again, you must embark on an epic quest to save Ferelden. Create your hero with detailed customisation options, make real choices that affect your path and the future of Ferelden itself, and decide how to battle the forces against you. Dragon Age: Origins is a bigger, more beautiful and deeper adventure than any you\'ll have ever taken before.', null, null, '1306621381', null, null, 'M - Mature', '0', '0', '0', '0', '', 'no', '2011-05-31 04:18:40', '0', 'tv.com', 'No', null, null, '1', null, '', '', '', '', '', '', null, null, null, null);
INSERT INTO `games` VALUES ('869', 'Breath of Fire', null, '0', '04/03/1993', 'Capcom', 'Squaresoft', null, '|Role-Playing|', null, 'Long ago in the days of darkness and magic, a young wanderer comes face to face with his destiny. You control the fate of Ryu, a young warrior and descendant of the Light Dragon Clan. Together, with your mystical friend, Nina, you must search for magic spells and mysterious secrets. Along the way, gain experience and skill as you battle against strange monsters. Uncover valuable secrets that will help you on your adventure and unlock the secrets of the Dragon Clan.', '0', null, '1300450285', null, null, 'E - Everyone', '0', '0', '0', '0', '', 'no', '2011-05-31 04:18:40', '0', 'tv.com', 'No', null, null, '6', null, '', '', '', '', '', '', null, null, null, null);
