INSERT INTO `games` VALUES ('877', 'James Cameron\'s Avatar: The Game', null, '0', '12/01/2009', 'Ubisoft', 'Ubisoft', null, '|Action|', null, 'The game will take you deep into the heart of Pandora, an alien planet that is beyond imagination. Gamers will encounter the Na\'vi, Pandora\'s indigenous people and discover creatures and other wildlife the likes of which have never been seen in the world of video games before. When conflict erupts between the RDA Corporation, a space-faring consortium in search of valuable resources, and the Na\'vi, players will find themselves thrust into a fight for the heart of a planet and the fate of a civilization.', null, null, '1306633229', null, null, 'T - Teen', '0', '0', '0', '0', '', 'no', '2011-05-31 04:18:40', '0', 'tv.com', 'No', null, null, '1', null, '', '', '', '', '', '', null, null, null, null);
