INSERT INTO `games` VALUES ('929', 'Shank', null, '2', '08/24/2010', 'Klei Entertainment', 'EA', null, '|Action|', null, 'Shank is a side scrolling beat \'em up with a comic book art style. In the game the player controls Shank, an ex-mob hitman. The game features three main types of weaponry, knives, heavy melee weapons such as a chainsaw and a pair of pistols. Each weapon is assigned to a controller button, and the attacks can be combined to perform various combos. The player can collect temporary-use weaponry from fallen enemies, such as machine guns and rifles, as well as grenades.', '0', null, '1296428151', null, null, 'M - Mature', '0', '0', '0', '0', '', 'no', '2011-05-31 04:18:40', '0', 'tv.com', 'No', null, null, '1', null, '', '', '', '', '', '', null, null, null, null);
