INSERT INTO `games` VALUES ('932', 'Mafia II', null, '1', '08/24/2010', '2K Czech', '2K Games', null, '|Action|Adventure|Shooter|', null, 'Born the son of a poor immigrant, Vito is a beaten down Italian American who is trying to secure his piece of the American Dream. Looking to escape the life of poverty that consumed his childhood, Vito is soon swayed by the lure of power and wealth that a life of Organized Crime can bring.\r\nA petty criminal his whole life, Vito, along with his childhood friend, Joe, will descend into the world of Organized Crime. Together, they will work to prove themselves to the Mob as they try to make their names on the streets of a cold and unforgiving city.\r\nMafia 2 immerses players in the mob underworld of a fictitious late 1940\'s / early 1950\'s scenario. The game engages you in a cinematic Hollywood movie experience in a living, breathing city, fusing high octane gunplay with white knuckle driving and an engaging narrative.', '0', null, '1296450225', null, null, 'M - Mature', '0', '0', '0', '0', '', 'no', '2011-05-31 04:18:40', '0', 'tv.com', 'No', null, null, '1', null, '', '', '', '', '', '', null, null, null, null);
INSERT INTO `games` VALUES ('933', 'F1 2010', null, '4', '09/22/2010', 'Codemasters Birmingham', 'Codemasters', null, '|Racing|Sports|', null, 'Immerse yourself in the glamour, pressure and exhilaration of the world’s most exciting motorsport as F1 2010 roars onto the track complete with all the official drivers, teams and circuits set to feature in the highly anticipated 2010 FIA FORMULA ONE WORLD CHAMPIONSHIP™.\r\n\r\nCompete against FIA FORMULA ONE WORLD CHAMPIONS™ like Michael Schumacher, Lewis Hamilton, Jenson Button and Fernando Alonso and take on the full 2010 circuit line up featuring the return of the Canadian Grand Prix, Abu Dhabi and Singapore’s dramatic night race realised in glorious high definition for the first time and debut ahead of the drivers on the all-new Korean circuit.\r\n\r\nPowered by Codemasters’ EGO Game Technology Platform, the world of FORMULA ONE comes to life with jaw-dropping visuals, authentic handling, dynamic weather and class-leading damage in a range of game modes including quick race, career mode and online multiplayer.\r\n\r\nF1 2010 will take you to the heart of FORMULA ONE like never before.', null, null, '1306553536', null, null, 'E - Everyone', '0', '0', '0', '0', '', 'no', '2011-05-31 04:18:40', '0', 'tv.com', 'No', null, null, '1', null, '', '', '', '', '', '', null, null, null, null);
