INSERT INTO `games` VALUES ('948', 'Colin McRae: Dirt 3', null, '0', '05/24/2011', 'Codemasters', 'Codemasters', null, '|Racing|', null, 'DiRT 3 will boast more cars, more locations, more routes and more events than any other game in the series, including over 50 rally cars representing the very best from five decades of the sport. With more than double the track content of 2009\'s hit, DiRT 3 will see players start at the top as a professional driver, with a top-flight career in competitive off-road racing complimented by the opportunity to express themselves in Gymkhana-style showpiece driving events.\r\n\r\nAs players race to elevate their global standing, DiRT 3 delivers mud, sweat and gears world over: from the intense weather-beaten rally stages of Europe, Africa and the US, to executing performance driving showcases and career challenges where car control is pushed to spectacular limits.', null, null, '1306580448', null, null, 'E10+ - Everyone 10+', '0', '0', '0', '0', '', 'no', '2011-05-31 04:18:40', '0', 'tv.com', 'No', null, null, '1', null, '', '', '', '', '', '', null, null, null, null);
