INSERT INTO `games` VALUES ('957', 'Extreme-G', null, null, '10/25/1997', 'Probe Entertainment', 'Acclaim Entertainment', null, '|Shooter|', null, 'Extreme-G is set in the distant future where Earth is a mere wasteland. From their new found planet the human colonists watch with joy as their remote controlled power-bikes wreak havoc through their ancient cities. There is only one winner, the first to cross the line… or the last to survive.', null, null, '1286952306', null, null, 'E - Everyone', '0', '0', '0', '0', '', 'no', '2011-05-31 04:18:40', '0', 'tv.com', 'No', null, null, '3', null, '', '', '', '', '', '', null, null, null, null);
INSERT INTO `games` VALUES ('958', 'Extreme-G 2', null, null, '10/07/1998', 'Probe Entertainment', 'Acclaim Entertainment', null, '|Vehicle Simulation|Shooter|', null, 'This iteration, as with all Extreme-G games, is about futuristic racing: pilots race plasma-powered Tron-like bikes in an intergalactic Grand Prix at speeds that are over 999 mph. It is possible to break the sound barrier in this game, creating a sonic boom. While travelling at supersonic speeds, all game sounds are muted except the sound of the vehicle travelling. If the bike slows down to below supersonic speeds, another sonic boom can be heard and all game sounds will resume. The emphasis is on speed and creative racetrack design, with tracks looping through all three dimensions like roller coasters.', null, null, '1286952678', null, null, 'E - Everyone', '0', '0', '0', '0', '', 'no', '2011-05-31 04:18:40', '0', 'tv.com', 'No', null, null, '3', null, '', '', '', '', '', '', null, null, null, null);
