INSERT INTO `games` VALUES ('2478', 'Lara Croft and the Guardian of Light', null, '2', '08/18/2010', 'Crystal Dynamics', 'Eidos Interactive', null, '|Action|', null, 'Lara Croft and the Guardian of Light is a fixed-camera 3D platform with an isometric viewpoint action video game developed by Crystal Dynamics and published by Square Enix for the PC, PlayStation Network, Xbox Live Arcade and the iOS as part of the Tomb Raider series for digital download. Unlike previous games in the series, the game does not carry the Tomb Raider brand and has a heavy emphasis on cooperative gameplay. In multiplayer, players take the role as either Lara Croft or a 2,000 year old Mayan warrior named Totec. They must work together in order to stop the evil spirit Xolotl and retrieve the Mirror of Smoke. A single-player campaign mode is available that does not include the non-playable character AI following or helping Lara.', null, null, '1306632289', null, null, 'T - Teen', '0', '0', '0', '0', '', 'no', '2011-05-31 04:18:40', '0', 'tv.com', 'No', null, null, '1', null, '', '', '', '', '', '', null, null, null, null);
