INSERT INTO `games` VALUES ('1120', 'Bass Hunter 64', null, '1', '06/30/1999', 'Take 2 Interactive', 'Take 2 Interactive', null, '|Sports|', null, 'Go fishing in the great outdoors with Bass Hunter 64! Reel in a record-setting bass in tournament mode. 3D rendered environments, complete with native wildlife, and geographically accurate landscapes. Fish Pennsylvania\'s Lake Arthur and Florida\'s Butler Chain Lakes. Driving, underwater and positional cameras are throughout your adventure. Lure, strike, and fish-fighting animations designed to simulate bass fishing\'s most exciting moments. Fish behavior based on scientific research and practical experience of experts at In-Fisherman', '0', null, '1290621686', null, null, 'E - Everyone', '0', '0', '0', '0', '', 'no', '2011-05-31 04:18:40', '0', 'tv.com', 'No', null, null, '3', null, '', '', '', '', '', '', null, null, null, null);
