INSERT INTO `games` VALUES ('1145', 'Monopoly', null, '0', '05/01/1991', 'Sculptured', 'Arcadia Systems', null, '|Strategy|', null, 'This special MONOPOLY game brings to life all the wheeling, dealing, money-making fun of the world\'s most popular board game - and adds the fast action, hands-on excitement of the NES! There\'s visual entertainment with the animated Rich Uncle Pennybags and Community Chest and Chance Cards, and the speedy sports car and other tokens that zoom around the board. Now listen for voice enhancements such as the \"jailer\" who kicks Rich Uncle Pennybags out of Jail, and the digitized sound of the train whistles on the Railroad spaces. Like a real estate big shot, you let computer \"accounting\" keep track of your cash, assets, properties, houses and hotels. You can play with real, live real estate moguls, or take on the computer opponents, who might be beginners - or savvy big-time traders! The MONOPOLY game everyone grows up with, plus the NES... all in all, it\'s a real sweet deal!', null, null, '1306638144', null, null, 'E - Everyone', '0', '0', '0', '0', '', 'no', '2011-05-31 04:18:40', '0', 'tv.com', 'No', null, null, '7', null, '', '', '', '', '', '', null, null, null, null);
INSERT INTO `games` VALUES ('1147', 'Mortal Kombat 4', null, '2', '06/23/1998', 'Eurocom Ent.', 'Midway', null, '|Fighting|', null, 'Shao Kahn has been defeated at the hands of Earth\'s warriors, and now Shinnok has escaped to his confines in the Netherealm. The war has resumed once again. This time, the battle CAN be won by mortals!', '0', null, '1290902581', null, null, 'M - Mature', '0', '0', '0', '0', '', 'no', '2011-05-31 04:18:40', '0', 'tv.com', 'No', null, null, '3', null, '', '', '', '', '', '', null, null, null, null);
