INSERT INTO `games` VALUES ('1149', 'Mortal Kombat Trilogy', null, '2', '10/31/1996', 'Midway', 'Midway', null, '|Fighting|', null, 'Shao Kahn\'s final attempt to control the earth is upon us... 26 immediately playable characters plus 4 hidden characters! The new Aggressor Kombat Mode arms the quick and merciless with more strength and power to inflict heavy, damaging combinations! Over 30 moves and \"-Alities\" never seen in the arcade. New Brutalities let you pound opponents \'til they explode! New 3-on-3 Kombat Mode delivers a marathon fight-to-the-finish! 7 humiliating Stage Fatalities that send defeated opponents plummeting out of the arena to a grizzly death!', null, null, '1306630899', null, null, 'M - Mature', '0', '0', '0', '0', '', 'no', '2011-05-31 04:18:40', '0', 'tv.com', 'No', null, null, '3', null, '', '', '', '', '', '', null, null, null, null);
