INSERT INTO `games` VALUES ('1175', 'Off Road Challenge', null, '0', '1997', 'Midway', 'Midway', null, '|Racing|', null, 'The END of the ROAD is only the BEGINNING. Strap yourself in and get ready to ride! Players battle the elements and each other in a race that takes them across 6 rugged courses including the Mojave Desert, Las Vegas and Pike\'s Peak! Choose Circuit Mode and climb your way to the top of the heap. Course terrain includes snow, water, asphalt, dirt, sand, rocks and mud. Four selectable trucks and four hidden trucks. Players can customize trucks in the Speed Shop.', null, null, '1306621859', null, null, null, '0', '0', '0', '0', '', 'no', '2011-05-31 04:18:40', '0', 'tv.com', 'No', null, null, '3', null, '', '', '', '', '', '', null, null, null, null);
INSERT INTO `games` VALUES ('1205', 'Space Station Silicon Valley', null, '0', '10/01/1998', 'DMA Design', 'Take-Two Interactive', null, null, null, 'A space station that was built more than 1000 years before by Earth and, shortly after its launch, vanished. The station has since then mysteriously reappeared and it\'s bound on a collision course with Earth. Unless Dan and Evo can stop it, all will surely perish. The idea behind the game is fairly simplistic: players take on the role of Evo, a robotic microchip that possesses the unique ability to take over the bodies of dead (robotic) animals. Evo makes his way through a variety of levels and worlds filled with different objectives and, of course, different animals.', '0', null, '1302121216', null, null, 'E - Everyone', '0', '0', '0', '0', '', 'no', '2011-05-31 04:18:40', '0', 'tv.com', 'No', null, null, '3', null, '', '', '', '', '', '', null, null, null, null);
