INSERT INTO `games` VALUES ('1294', 'Goal!', null, null, '12/22/1988', 'TOSE', 'Jaleco', null, null, null, 'The game has four modes: World Cup, Tournament, Shoot Competition, and Vs. Mode. The object of the game is the same as in real football; one team must score more goals than the other to win.', '0', null, '1288928582', null, null, null, '0', '0', '0', '0', '', 'no', '2011-05-31 04:18:40', '0', 'tv.com', 'No', null, null, '7', null, '', '', '', '', '', '', null, null, null, null);
INSERT INTO `games` VALUES ('1295', 'Golgo 13: Top Secret Episode', null, null, '03/26/1988', 'Vic Tokai, Seibu Lease', 'Vic Tokai', null, '|Action|Adventure|', null, 'The player takes on the role of Golgo 13 (also known as Duke Togo), an assassin whose objective is to destroy the leader of the Drek group. On the way, Golgo 13 must pass through several areas, including East Berlin, Athens, and Alexander Island, which is located off the coast of Antarctica. It is necessary to navigate several mazes in order to win, but one maze is a fake base with no way out. The mazes account for a large amount of the frustration players encounter with this game. The instruction manual contains maps for the mazes.', '0', null, '1288929096', null, null, null, '0', '0', '0', '0', '', 'no', '2011-05-31 04:18:40', '0', 'tv.com', 'No', null, null, '7', null, '', '', '', '', '', '', null, null, null, null);
INSERT INTO `games` VALUES ('1296', 'The Goonies II', null, null, '03/18/1987', 'Konami', 'Konami', null, '|Adventure|', null, 'The Goonies II features two modes of play: platform and first-person. Most of the game is played as the former as the player works through a non-linear map. The player moves Mikey to new areas of the map by ladders or doors that may act as warp zones. Several different types of zones are found in the game, each with distinct enemies, graphics and music.', '0', null, '1288929339', null, null, 'E - Everyone', '0', '0', '0', '0', '', 'no', '2011-05-31 04:18:40', '0', 'tv.com', 'No', null, null, '7', null, '', '', '', '', '', '', null, null, null, null);
