INSERT INTO `games` VALUES ('1499', 'Puzzle Bobble', null, '2', '07/01/1999', 'SunSoft', 'SunSoft', null, '|Puzzle|', null, 'Puzzle Bobble is the ultimate in cute and fun action puzzle games.  The rules are easy; aim left and right and shoot your bubbles.  When three or more of the same color bubbles are joined together, they burst.  The greater the number of bubbles you burst at the same time, the more points you get.  One player versus the computer or against a friend;  It\'s easy and fun to play.', null, null, '1290627017', null, null, null, '0', '0', '0', '0', '', 'no', '2011-05-31 04:18:40', '0', 'tv.com', 'No', null, null, '24', null, '', '', '', '', '', '', null, null, null, null);
