INSERT INTO `games` VALUES ('1501', 'Abadox', null, '1', '12/15/1989', 'Natsume', 'Natsume', null, '|Shooter|', null, 'In the year 5012, the planet known as Abadox is soon eaten by a giant alien organism known as Parasitis. Once the planet has been consumed, the alien takes the form of the entire planet and seeks to devour other planets within the galaxy. The galactic military tries to fight the alien, but to no avail as Parasitis destroys the entire galactic military, except for one hospital ship. It is then discovered that Princess Maria has been devoured as well. The galactic military decides to send out their best fighter, \"Second Lieutenant Nazal\", to attempt to enter Parasitis\' body and rescue Princess Maria before it\'s too late.', '0', null, '1290569324', null, null, null, '0', '0', '0', '0', '', 'no', '2011-05-31 04:18:40', '0', 'tv.com', 'No', null, null, '7', null, '', '', '', '', '', '', null, null, null, null);
