INSERT INTO `games` VALUES ('1503', 'Advanced Dungeons & Dragons: DragonStrike', null, '1', '1990', 'Westwood Associates', 'Strategic Simulations', null, '|Role-Playing|', null, 'The game is set in Krynn, world of the Dragonlance saga, during the War of the Lance. DragonStrike combined elements of computer role-playing games and flight simulators. The player character is a knight who flies on the back of a metallic dragon equipped with a lance and various magic items (among other things a magic orb that acts as a radar in the game).', null, null, '1306543425', null, null, null, '0', '0', '0', '0', '', 'no', '2011-05-31 04:18:40', '0', 'tv.com', 'No', null, null, '7', null, '', '', '', '', '', '', null, null, null, null);
