INSERT INTO `games` VALUES ('1509', 'Adventure Island III', null, '1', '07/31/1992', 'Hudson Soft', 'Hudson Soft', null, '|Action|Adventure|', null, 'The gameplay and the design haven\'t much changed since Adventure Island II: Aliens in Paradise: there is a world map, you can choose items before starting a level, the levels themselves are quite short (unlike Adventure Island, where each large level contained several stages). You perform standard \"adventure island\" activities: ride various types of dinosaurs, throw boomerangs at snails, birds, snakes, and skulls, collect grapes and apples, jump over stones, and of course, ride your old trusty skateboard.', '0', null, '1290924109', null, null, null, '0', '0', '0', '0', '', 'no', '2011-05-31 04:18:40', '0', 'tv.com', 'No', null, null, '7', null, '', '', '', '', '', '', null, null, null, null);
