INSERT INTO `games` VALUES ('1515', 'Barkley Shut Up and Jam!', null, '2', '06/01/1994', 'Accolade', 'Den\'Z', null, '|Sports|', null, 'In the game you play street basketball two-on-two with your friends on with computer. So there are no referees, no fouls, and no shot clock, but the teams called by the names of cities such as Phoenix or Chicago. You can choose your player from 16 available players, including Barkley himself.', null, null, '1306623818', null, null, null, '0', '0', '0', '0', '', 'no', '2011-05-31 04:18:40', '0', 'tv.com', 'No', null, null, '6', null, '', '', '', '', '', '', null, null, null, null);
