INSERT INTO `games` VALUES ('1570', 'Arkanoid Doh It Again', null, '1', '01/01/1997', 'Taito', 'Romstar', null, '|Puzzle|Sandbox|', null, 'Much like the game Breakout, the player controls the \"Vaus\", a space vessel that acts as the game\'s \"paddle\" which prevents a ball from falling from the playing field, attempting to bounce it against a number of bricks. The ball striking a brick causes the brick to disappear. When all the bricks are gone, the player goes to the next level, where another pattern of bricks appear. There are a number of variations (bricks that have to be hit multiple times, flying enemy ships, etc.) and power-up capsules to enhance the Vaus (expand the Vaus, multiply the number of balls, equip a laser cannon, break directly to the next level, etc.), but the gameplay remains the same.', '0', null, '1306264818', null, null, 'E - Everyone', '0', '0', '0', '0', '', 'no', '2011-05-31 04:18:40', '0', 'tv.com', 'No', null, null, '6', null, '', '', '', '', '', '', null, null, null, null);
INSERT INTO `games` VALUES ('1571', 'Art of Fighting', null, '0', '09/24/1992', 'SNK', 'SNK', null, '|Fighting|', null, 'The gameplay differs from most fighting games. In this game, you have a Spirit meter. A Spirit meter is in the game and will decrease as you perform special moves. It can be recharged, but doing so leaves you open to attack. When your health is almost gone, you can perform a secret combination that will unleash a deadly attack on your opponent - provided you have a full Spirit meter.', null, null, '1306564179', null, null, 'T - Teen', '0', '0', '0', '0', '', 'no', '2011-05-31 04:18:40', '0', 'tv.com', 'No', null, null, '6', null, '', '', '', '', '', '', null, null, null, null);
