INSERT INTO `games` VALUES ('1714', 'Bill Laimbeer\'s Combat Basketball', null, '2', '11/01/1991', 'Hudson Soft', 'Hudson Soft', null, '|Action|Sports|', null, 'The game predicts a science-fiction version of the year 2030 in which there are only robot basketball players (excluding Bill Laimbeer). Basketball teams play in gruelling league matches where new players are bought and sold. Within this future, basketball uses a dedicated robot to perform the toss up at the start of each match as referees had been fired by Bill Laimbeer sometime prior to the year 2030. As a result, players now wear armor to their games and weapons are thrown from the audience.', '0', null, '1290590545', null, null, 'E - Everyone', '0', '0', '0', '0', '', 'no', '2011-05-31 04:18:40', '0', 'tv.com', 'No', null, null, '6', null, '', '', '', '', '', '', null, null, null, null);
INSERT INTO `games` VALUES ('1784', 'The Sims 3', null, '1', '06/02/2009', 'Visceral Games', 'Electronic Arts', null, '|Construction and Management Simulation|Life Simulation|', null, 'The Sims 3 allows you to immerse your unique Sims in an open living neighborhood right outside their door. The initial feature-set unveiled includes the new seamless, open neighborhood, new Create-A-Sim, new realistic personalities(every Sim is a unique person, with a distinct personality) and new unlimited customization, You can customize everything!', null, null, '1304022872', null, null, 'T - Teen', '0', '0', '0', '0', '', 'no', '2011-05-31 04:18:40', '0', 'tv.com', 'No', null, null, '1', null, '', '', '', '', '', '', null, null, null, null);
INSERT INTO `games` VALUES ('1716', 'BlaZeon: The Bio-Cyborg Challenge', null, '2', '1992', 'Dandy Arai', 'Atlus', null, '|Shooter|', null, 'The player is set in control of The Garland TFF-01, a sub-standard space fighter ship that is only armed with a semi-auto or rapid fire laser gun known as the Beam Vulcan and the Tranquilander. Some enemy units can be captured and used in play; certain enemy robots, when shot by the player\'s Tranquilander, will morph into an outline of themselves. The player can then fly into the outline, and take on the form of the enemy robot, taking control of its weapons, agilities and special attacks.', null, null, '1306564691', null, null, null, '0', '0', '0', '0', '', 'no', '2011-05-31 04:18:40', '0', 'tv.com', 'No', null, null, '6', null, '', '', '', '', '', '', null, null, null, null);
INSERT INTO `games` VALUES ('1717', 'Boogerman: A Pick and Flick Adventure', null, '1', '1994', 'Interplay', 'Interplay', null, '|Action|Adventure|', null, 'You will have an adventure in an unknown world as a caped hero who slings his own boogers to vanquish his foes. The hero of the game is a booger-tossing, noisily burping, fart-spewing man named Snotty Ragsdale. While he lives his normal life as an eccentric millionaire, his natural \"in-stinks\" come in handy when he dons the mask of the smelly hero Boogerman.', null, null, '1306564848', null, null, 'E - Everyone', '0', '0', '0', '0', '', 'no', '2011-05-31 04:18:40', '0', 'tv.com', 'No', null, null, '6', null, '', '', '', '', '', '', null, null, null, null);
INSERT INTO `games` VALUES ('1718', 'Brain Lord', null, '1', '01/29/1994', 'Produce', 'Enix', null, '|Role-Playing|', null, 'In \"Brain Lord\", you play a young traveler and warrior who is searching for his long lost father. Long ago, when you were a mere child, your father told you about the great legacy of your village. Once your people were Dragon Warriors, who could control and fly on dragons. But the dragons had disappeared, and your father was determined to find the legendary Tower of Light, where, according to rumors, the last dragons might be hiding. You father went to search the Tower of Light and never came back. Years have passed, and now you finally found the Tower of Light. But your adventure doesn\'t end there, as you had thought - it begins there...', '0', null, '1290590966', null, null, null, '0', '0', '0', '0', '', 'no', '2011-05-31 04:18:40', '0', 'tv.com', 'No', null, null, '6', null, '', '', '', '', '', '', null, null, null, null);
INSERT INTO `games` VALUES ('1719', 'Bronkie the Bronchiasaurus', null, '2', '09/01/1995', 'WaveQuest', 'Raya Systems', null, '|Action|Adventure|', null, 'You must help Bronkie and Rexo find the lost machine pieces by blowing away dust with your lungs, and hitting enemies with your dusters, before the dust returns and everyone chokes on the dust! But don\'t run into cigarrette smoke or dust storms, otherwise your vision will get darker, and you\'ll have a hard time finding out where to go, running into enemies all around!', '0', null, '1290591342', null, null, null, '0', '0', '0', '0', '', 'no', '2011-05-31 04:18:40', '0', 'tv.com', 'No', null, null, '6', null, '', '', '', '', '', '', null, null, null, null);
INSERT INTO `games` VALUES ('1720', 'Brutal: Paws of Fury', null, '2', '1994', 'GameTek', 'GameTek', null, '|Fighting|', null, 'Brutal: Paws of Fury is a standard one-on-one beat \'em up across a variety of levels, with mostly outdoor terrain. You can choose of the many characters. Throughout the game, your character travels across Dalai Llama\'s island, fighting a series of duels before getting to the final showdown.', null, null, '1306565001', null, null, null, '0', '0', '0', '0', '', 'no', '2011-05-31 04:18:40', '0', 'tv.com', 'No', null, null, '6', null, '', '', '', '', '', '', null, null, null, null);
INSERT INTO `games` VALUES ('1721', 'Bubsy in: Claws Encounters of the Furred Kind', null, '1', '1993', 'Accolade', 'Accolade', null, '|Action|Adventure|', null, 'A race of fabric-stealing aliens called \"Woolies\", who have stolen the world\'s yarn ball supply (especially Bubsy\'s, who owns the world\'s largest collection). Naturally, Bubsy does not take too kindly to the theft and sets out to \"humble\" the Woolies.', '0', null, '1290591630', null, null, null, '0', '0', '0', '0', '', 'no', '2011-05-31 04:18:40', '0', 'tv.com', 'No', null, null, '6', null, '', '', '', '', '', '', null, null, null, null);
INSERT INTO `games` VALUES ('1722', 'Bubsy II', null, '1', '04/03/1994', 'Accolade', 'Accolade', null, '|Action|Adventure|', null, 'A new historical theme park, the Amazotarium, has been made. Though impressive, the park\'s sinister technology is stealing away actual history and placing it on exhibit. Having taken his niece and nephew to the park, Bubsy loses them and is soon caught up in the evil plans of Oinker P. Hamm, who runs Amazaterium and wants to extort money from his unwitting customers who\'ll \"pay anything to see their own history\". Now, Bubsy needs to \"humble\" Oinker and close the fascinating-but-deadly Amazatorium before anyone faces a hairy ending.', '0', null, '1293527733', null, null, null, '0', '0', '0', '0', '', 'no', '2011-05-31 04:18:40', '0', 'tv.com', 'No', null, null, '6', null, '', '', '', '', '', '', null, null, null, null);
