INSERT INTO `games` VALUES ('1752', 'Dino City', null, '2', '07/17/1992', 'Irem', 'Irem', null, '|Adventure|', null, 'Timmy and his friend, Jaime go into Timmy\'s father\'s lab to watch a movie on the big screen. Little do they know that the big screen is actually a time machine. When Timmy touches the remote they are both sucked into prehistoric times. Now in order to return home they must reclaim a time machine fuse that was stolen by some Neanderthals who go by the name of \"The Rockies\". The Rockies are also looking to destroy DinoCity, so two dinosaurs named Rex and Tops decide to help out the two human children in their fight against the Neanderthals.', '0', null, '1290735838', null, null, null, '0', '0', '0', '0', '', 'no', '2011-05-31 04:18:40', '0', 'tv.com', 'No', null, null, '6', null, '', '', '', '', '', '', null, null, null, null);
INSERT INTO `games` VALUES ('1753', 'Dino Dini\'s Soccer', null, '2', '1994', 'Dino Dini', 'Virgin Gamers', null, '|Sports|', null, 'Although this soccer game is based on Goal, it features a very different control method, with the ball on sticking to your foot to make running with the ball easier. The game is viewed from above and played up and down the pitch, without the left-to-right option of Goal. Matches can be between 2 or 20 minutes, and the scanner which shows player positions on the rest of the pitch can be moved, resized or disabled. The method of triggering aftertouch onto a pass can be chosen as well.', null, null, '1306578564', null, null, null, '0', '0', '0', '0', '', 'no', '2011-05-31 04:18:40', '0', 'tv.com', 'No', null, null, '6', null, '', '', '', '', '', '', null, null, null, null);
