INSERT INTO `games` VALUES ('1759', 'Drakkhen', null, '1', '1989', 'Draconian, Kemco-Seika', 'Infogrames', null, '|Role-Playing|', null, 'Drakkhen is a role-playing game with several ingenious features for its time. The eye-catcher is a mix of fast 3D landscapes and 2D interiors. The player\'s party will travel overland on a flat terrain made up of vectors, on which 2D objects are zoomed (The same type of engine was later used in Eternam). The player will encounter monsters and fight them in real-time combat, and discover the palaces of the dragon lords. Indoors, the player has to find a way through the maze of chambers, solving puzzles and defeating the minions of the Drakkhen. Party members are depicted in four status windows on the left side of the screen, thus allowing to see their current status and equipment at once.', '0', null, '1290931855', null, null, null, '0', '0', '0', '0', '', 'no', '2011-05-31 04:18:40', '0', 'tv.com', 'No', null, null, '6', null, '', '', '', '', '', '', null, null, null, null);
