INSERT INTO `games` VALUES ('1761', 'Final Fantasy II', null, '0', '07/19/1991', 'Square', 'Square', null, '|Role-Playing|', null, 'With its character-driven plot, use of new technologies and critically acclaimed score by Nobuo Uematsu, Final Fantasy IV (known as Final Fantasy 2 in the U.S.) is regarded as a landmark of the series and of the role-playing genre. It is considered to be one of the first role-playing games to feature a complex, involving plot, and is thought to have pioneered the idea of dramatic storytelling in an RPG. The various incarnations of the game have sold more than four million copies worldwide. A sequel to the game, Final Fantasy IV: The After Years, was released for Japanese mobile phones in 2008, and worldwide via the Wii Shop Channel on June 1, 2009.', null, null, '1305961934', null, null, 'T - Teen', '0', '0', '0', '0', '', 'no', '2011-05-31 04:18:40', '0', 'tv.com', 'No', null, null, '6', null, '', '', '', '', '', '', null, null, null, null);
