INSERT INTO `games` VALUES ('1787', 'Chester Cheetah: Too Cool to Fool', null, '1', '12/01/1992', 'Kaneko', 'Kaneko', null, '|Action|Adventure|', null, 'The game\'s hero is the Frito-Lay mascot, Chester the Cheetah. Chester leaves peacefully in a zoo with other animals, until the big brute Mean Eugene steals his beloved motor scooter. What\'s worse, without knowing how to handle the scooter, Mean Eugene broke it to pieces and threw them away. Now it\'s up to Cheetah to travel across the zoo and to retrieve parts of his scooter.', null, null, '1306565940', null, null, null, '0', '0', '0', '0', '', 'no', '2011-05-31 04:18:40', '0', 'tv.com', 'No', null, null, '18', null, '', '', '', '', '', '', null, null, null, null);
INSERT INTO `games` VALUES ('1788', 'Clayfighter: Tournament Edition', null, '0', '05/01/1994', 'Interplay Productions, Inc', 'Visual Concepts Entertainment, Inc', null, '|Fighting|', null, 'The Tournament Edition of Clay Fighter fixes some glitches and adds new features to the original 2D fighting game. Most scenarios were changed or redone, the graphics are brighter and the gameplay is faster. The characters can now perform three and four-hit combos. More voice-overs and a new introduction cut-scene were added.', '0', null, '1290733576', null, null, null, '0', '0', '0', '0', '', 'no', '2011-05-31 04:18:40', '0', 'tv.com', 'No', null, null, '6', null, '', '', '', '', '', '', null, null, null, null);
INSERT INTO `games` VALUES ('1789', 'Clue', null, '2', '1998', 'Engineering Animation, Inc.', 'Hasbro Interactive', null, '|Puzzle|Strategy|', null, 'In addition to play by the original rules, Clue has an additional mode that allows movement via \"points.\" Each turn begins with nine points and every action the player takes costs points. The player can only do as many things as he has points. For example, moving from square to square costs one point, making a suggestion costs three points. Many players prefer this mode of play as it makes the game more balanced since each player gets the same number of \"moves\" each turn.', '0', null, '1290734459', null, null, 'T - Teen', '0', '0', '0', '0', '', 'no', '2011-05-31 04:18:40', '0', 'tv.com', 'No', null, null, '6', null, '', '', '', '', '', '', null, null, null, null);
INSERT INTO `games` VALUES ('1790', 'The Combatribes', null, '3', '06/01/1990', 'Technos Japan Corp.', 'American Technos Inc.', null, '|Fighting|', null, 'A 2D side-scrolling brawler, The Combatribes puts you and a friend in the shoes of combat cyborgs Berzeker, Bullova and Blitz (yes those are their real names) as they attempt to defeat the evil crime ring \"Ground Zero\", led by the cybernetically enhanced Martha Splatterhead and her minions.', null, null, '1306567703', null, null, null, '0', '0', '0', '0', '', 'no', '2011-05-31 04:18:40', '0', 'tv.com', 'No', null, null, '6', null, '', '', '', '', '', '', null, null, null, null);
